import api from '@/lib/api';
import { toast } from 'sonner';

export default function QueryPage() {
    return (
        <div className="space-y-4 sm:space-y-6 pb-8">
            {/* Header */}
            <div>
                <h2 className="text-2xl sm:text-3xl font-bold tracking-tight">Query Explorer</h2>
                <p className="text-sm text-muted-foreground mt-1">Search and analyze code across your repositories</p>
            </div>

            {/* Query Tabs */}
            <Tabs defaultValue="search" className="w-full">
                <TabsList className="grid w-full grid-cols-3 lg:grid-cols-9 h-auto p-1 gap-1">
                    <TabsTrigger value="search" className="flex flex-col sm:flex-row items-center gap-1 sm:gap-2 py-2 text-xs sm:text-sm">
