                        <span>CodeFinder</span>
                    </TabsTrigger>
                    <TabsTrigger value="complexity" className="flex flex-col sm:flex-row items-center gap-1 sm:gap-2 py-2 text-xs sm:text-sm">
                        <AlertCircle className="h-4 w-4" />
                        <span>Complexity</span>
                    </TabsTrigger>
                    <TabsTrigger value="relationships" className="flex flex-col sm:flex-row items-center gap-1 sm:gap-2 py-2 text-xs sm:text-sm">
                        <GitBranch className="h-4 w-4" />
                        <span>Relations</span>
                    </TabsTrigger>
                    <TabsTrigger value="language" className="flex flex-col sm:flex-row items-center gap-1 sm:gap-2 py-2 text-xs sm:text-sm">
                        <FileCode className="h-4 w-4" />
                        <span>Language</span>
