export interface SearchResult {
  type: string;
  score: number;
  file: string;
  node: {
    name: string;
    line_start?: number;
    line_end?: number;
    docstring?: string;
    source_code?: string;
  };
}

export interface ClassHierarchy {
  class_name: string;
  ancestors: { name: string }[];
  descendants: { name: string }[];
}

export interface SymbolDefinition {
  name: string;
  type: string;
  file: string;
  line: number;
  docstring?: string;
  source_code?: string;
}

export interface CodeFinderResult {
  total: number;
  results: {
    name: string;
    path: string;
    line_number: number;
    docstring?: string;
    value?: string;
    is_dependency?: boolean;
  }[];
}

export interface RelatedCodeResult {
  total_matches: number;
  ranked_results: {
    name: string;
    path: string;
    line_number: number;
    search_type: string;
    relevance_score: number;
    docstring?: string;
    is_dependency?: boolean;
  }[];
}

export interface ComplexityResult {
  complexity: {
    function_name: string;
    path: string;
    line_number: number;
    complexity: number;
  };
}

export interface LanguageSymbolResult {
  total: number;
  language: string;
  results: {
    name: string;
    path: string;
    line_number: number;
    docstring?: string;
    file_language?: string;
    is_dependency?: boolean;
  }[];
}

export interface RelationshipQueryResult {
  summary?: string;
  results?: unknown[];
